@Override
public boolean onOptionsItemSelected(MenuItem item) {

String ktoryElement = "";

switch (item.getItemId()) {

case R.id.nav_dla_darczyncy:
ktoryElement = "pierwszy";
break;
case R.id.nav_foramcja:
ktoryElement = "drugi";
break;
case R.id.nav_komfor:
ktoryElement = "trzeci";
break;
case R.id.nav_kontakt:
ktoryElement = "czwarty";
break;
case R.id.nav_materialy:
ktoryElement = "piaty";
break;
case R.id.nav_materialy_prasowe:
ktoryElement = "szosty";
break;
case R.id.nav_nasz_patron:
ktoryElement = "siodmy";
break;
case R.id.nav_o_fundacji:
ktoryElement = "osmy";
break;
case R.id.nav_ogl_ogolne:
ktoryElement = "dziewiaty";
break;
case R.id.nav_ogl_wspolnotowe:
ktoryElement = "dziesiaty";
break;
case R.id.nav_poczta:
ktoryElement = "jedenasty";
break;
default:
ktoryElement = "Å¼aden";

}

Toast.makeText(getApplicationContext(), "Element: " + ktoryElement,
Toast.LENGTH_LONG).show();

return true;
}
